  // const [enterdAmont,setEnteredAmount] = useState("");
  // const [enteredDate,setEnteredDate] = useState("");
  //  we can get by one state
  const [userInput, setUserInput] = useState({
    setEnteredTitle: "",
    setEnteredAmount: "",
    setEnteredDate: "",
  });

  // const titleChangeHandler = (event) => {
